mapper.select:
select count(*) from user_cart
where cart_userid=#{userid2}
and cart_productid=#{productId}

mapper.update:
update user_cart set cart_count=cart_count+#{cartCount}
where cart_userid=#{userid2}
and cart_productid=#{productId}

update user_cart set cart_count=#{cartCount}
where cart_userid=#{userid2}
and cart_productid=#{productId}
