mapper.select:
SELECT * FROM PRODUCT_IMAGE
WHERE 1=1
	AND IMAGE_PRODUCT_ID = #{PRODUCT_ID}

SELECT PRODUCT_ID AS productId ,PRODUCT_NAME AS productName ,PRODUCT_PRICE AS productPrice ,PRODUCT_DELIVERY_PRICE AS productDeliveryPrice ,PRODUCT_MODEL AS productModel ,PRODUCT_MAKER AS productMaker ,PRODUCT_MADEIN AS productMadein ,PRODUCT_STOCK AS productStock ,PRODUCT_SELLER AS productSeller ,PRODUCT_REG_DATE AS productRegDate ,PRODUCT_PREVIEW AS productPreview ,PRODUCT_CONTENT AS productContent ,PRODUCT_CATEGORY AS productCategory FROM PRODUCT_DETAIL A ,PRODUCT_GUITAR_CATEGORY B WHERE 1=1 AND A.PRODUCT_CATEGORY = B.CATEGORY_ID
AND PRODUCT_CATEGORY = #{category}
AND ( LOWER(PRODUCT_NAME) LIKE '%' || LOWER(#{searchStr}) || '%' OR LOWER(PRODUCT_MODEL) LIKE '%' || LOWER(#{searchStr}) || '%' OR LOWER(PRODUCT_MAKER) LIKE '%' || LOWER(#{searchStr}) || '%' OR LOWER(PRODUCT_CONTENT) LIKE '%' || LOWER(#{searchStr}) || '%' OR LOWER(B.CATEGORY_NAME) LIKE '%' || LOWER(#{searchStr}) || '%' OR LOWER(B.CATEGORY_DESC) LIKE '%' || LOWER(#{searchStr}) || '%' )
